mapper.select:
SELECT * 
	FROM car 
	WHERE car_name LIKE CONCAT('%',#{car_name},'%');

mapper.update:
		UPDATE car
		SET car_manufacturer = #{car_manufacturer}
  			   , car_model = #{car_model}
  			   , car_name = #{car_name}
  			   , car_type = #{car_type}
  			   , car_year = #{car_year}
  			   , car_price = #{car_price}  			
  			   , car_seater = #{car_seater}
  			   , car_fuel = #{car_fuel}

  		WHERE car_id = #{car_id}

mapper.insert:


			SELECT IFNULL(MAX(car_id), 0) FROM car


  		INSERT INTO car
  		VALUES(#{car_id} + 1
  			   , #{car_manufacturer}
  			   , #{car_model}
  			   , #{car_name}
  			   , #{car_type}
  			   , #{car_year}
  			   , #{car_price}
  			   , '1'
  			   , #{car_seater}
  			   , #{car_fuel}
  			  )
