mapper.insert:
	insert into review (
	review_idx
  , member_email
  , member_name
  , review_title
  , review_content
  , review_regdate
  , review_updateDate
  , review_star
  , review_viewcnt
	)
	value (
	#{review_idx}
  , #{member_email}
  , #{member_name}
  , #{review_title}
  , #{review_content}
  , CURRENT_TIMESTAMP()
  , CURRENT_TIMESTAMP()
  , #{review_star}
  , 0
	)

mapper.select:
SELECT COUNT(member_email)
  FROM review
 WHERE member_email = #{member_email}
