mapper.insert:

	SELECT IFNULL(MAX(coup_idx), 0) FROM coupon


INSERT INTO coupon
VALUES(#{coup_idx} + 1
, #{coup_name}
, (SELECT SUBSTR(MD5(RAND()), 1, 8))
, #{coup_start}
, #{coup_end}
, #{coup_percent}
)

mapper.select:
SELECT member_email
FROM coupon_used
WHERE member_email = #{member_email}

SELECT coup_idx
FROM coupon_used
WHERE coup_idx = #{coup_idx}
	 AND member_email = #{member_email}
