mapper.insert:

	SELECT IFNULL(MAX(review_idx), 1) FROM review

INSERT INTO review 
			value (
				#{review_idx} + 1
			  , #{res_id}
			  , #{member_email}
			  ,	#{member_name}
			  , #{review_content}
			  , CURRENT_TIMESTAMP()
			  , #{review_star}
			  , #{review_place}
			)

mapper.select:
	 	SELECT *
 	 	  FROM review
 		 WHERE member_email 
 		 IN (SELECT member_email 
	 		 FROM member 
	 		 WHERE member_email = #{member_email}
	 		 ) 
 		 ORDER BY res_id DESC;

SELECT COUNT(member_email)
  FROM review
 WHERE member_email = #{member_email}
